import React from "react";
import "./Card.scss";
import { Link } from "react-router-dom";

const Card = ({ item }) => {
  // Check if 'item' is defined
  if (!item) {
    return null; // Return null or an appropriate fallback if 'item' is not defined
  }

  const { attributes } = item;

  return (
    <Link className="link" to={`/product/${item.id}`}>
      <div className="card">
        <div className="image">
          {/* Check if 'isNew' property exists and is true */}
          {attributes?.isNew && <span>New Season</span>}

          <img
            src={
              process.env.REACT_APP_UPLOAD_URL +
              attributes.img?.data?.attributes?.url
            }
            alt=""
            className="mainImg"
          />

          <img
            src={
              process.env.REACT_APP_UPLOAD_URL +
              attributes?.img2?.data?.attributes?.url
            }
            alt=""
            className="secondImg"
          />
        </div>
        <h2>{attributes?.title || "Title not available"}</h2>
        <div className="prices">
          {/* Display either 'oldPrice' or 'price + 20' */}
          <h3>${item.oldPrice || (attributes?.price + 20)}</h3>
          <h3>${attributes?.price}</h3>
        </div>
      </div>
    </Link>
  );
};

export default Card;
