import React from "react";
import "./Card.scss"; // Importing the CSS styles for the Card component
import { Link } from "react-router-dom"; // Importing the Link component from react-router-dom

// Card component definition
const Card = ({ item }) => {
  console.log(item); // Logging the 'item' prop received from the parent component
  return (
    <Link className="link" to={`/product/${item.id}`}> {/* Creating a Link that navigates to the product page */}
      <div className="card"> {/* Container for the card */}
        <div className="image"> {/* Container for the images */}
          {item?.attributes.isNew && <span>New Season</span>} {/* Display "New Season" span if 'isNew' attribute is true */}
          <img
            src={
              process.env.REACT_APP_UPLOAD_URL + item.attributes?.img?.data?.attributes?.url
            }
            alt=""
            className="mainImg"
          /> {/* Displaying the main product image */}
          <img
            src={
              process.env.REACT_APP_UPLOAD_URL + item.attributes?.img2?.data?.attributes?.url
            }
            alt=""
            className="secondImg"
          /> {/* Displaying the second product image */}
        </div>
        <h2>{item?.attributes.title}</h2> {/* Displaying the product title */}
        <div className="prices"> {/* Container for the prices */}
          <h3>${item.oldPrice || item?.attributes.price + 20}</h3> {/* Displaying the old price or current price + $20 */}
          <h3>${item?.attributes.price}</h3> {/* Displaying the current product price */}
        </div>
      </div>
    </Link>
  );
};

export default Card; // Exporting the Card component
