import { useEffect, useState } from "react";
import { makeRequest } from "../makeRequest"; // Asumiendo que makeRequest es un m칩dulo v치lido

const useFetch = (url) => {
  const [data, setData] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(false);

  useEffect(() => {
    const fetchData = async () => {
      try {
        setLoading(true);
        const response = await makeRequest.get(url); // Renombrado 'res' a 'response' para mayor claridad
        setData(response.data); // Se asume que response contiene directamente los datos
      } catch (err) {
        setError(true);
      } finally {
        setLoading(false);
      }
    };

    fetchData(); // Llamada inicial a fetchData
  }, [url]);

  return { data, loading, error };
};

export default useFetch;
