import React, { useState } from "react";
import { useParams } from "react-router-dom";
import List from "../../components/List/List";
import Card from "../../components/Card/Card"; // Import the Card component
import useFetch from "../../hooks/useFetch";
import item from "../../components/Card/Card";
import "./Products.scss";

const Products = () => {
  const catId = parseInt(useParams().id);
  const [maxPrice, setMaxPrice] = useState(1000);
  const [sort, setSort] = useState(null);
  const [selectedSubCats, setSelectedSubCats] = useState([]);

  // Fetch sub-categories for the selected category
  const { data: subCategories, loading, error } = useFetch(
    `/sub-categories?[filters][categories][id][$eq]=${catId}`
  );

  // Handle change in selected sub-categories
  const handleSubCategoryChange = (e) => {
    const value = e.target.value;
    const isChecked = e.target.checked;

    setSelectedSubCats((prevSelected) =>
      isChecked ? [...prevSelected, value] : prevSelected.filter((item) => item !== value)
    );
  };

  return (
    <div className="products">
      {/* Left section with filters */}
      <div className="left">
        {/* Filter by sub-categories */}
        <div className="filterItem">
          <h2>Product Categories</h2>
          {subCategories?.map((subCategory) => (
            <div className="inputItem" key={subCategory.id}>
              <input
                type="checkbox"
                id={subCategory.id}
                value={subCategory.id}
                onChange={handleSubCategoryChange}
              />
              <label htmlFor={subCategory.id}>{subCategory.attributes.title}</label>
            </div>
          ))}
        </div>
        {/* ... (rest of the filter options) */}
      </div>
      {/* Right section with product list */}
      <div className="right">
        {/* Display category image */}
        <img
          className="catImg"
          src="https://images.pexels.com/photos/1074535/pexels-photo-1074535.jpeg?auto=compress&cs=tinysrgb&w=1600"
          alt=""
        />
        {/* Render product list */}
        <List catId={catId} maxPrice={maxPrice} sort={sort} subCats={selectedSubCats}>
          {/* Pass a render function to the List component */}
          {(items) =>
            items.map((item) => (
              <Card key={item.id} item={item} />
            ))
          }
        </List>
      </div>
    </div>
  );
};

export default Products;
