import React from "react";
import Card from "../Card/Card";
import "./FeaturedProducts.scss";
import useFetch from "../../hooks/useFetch";

const FeaturedProducts = ({ type }) => {
  const { data, loading, error } = useFetch(
    `/products?populate=*&[filters][type][$eq]=${type}&[filters][img][$exists]=true`
  );

  return (
    <div className="featuredProducts">
      <div className="top">
        <h1>{type} products</h1>
        <p>
        Discover the latest trends and technologies across various categories, all meticulously chosen to elevate your everyday experiences. From smart home solutions that streamline your routines to fashion-forward accessories that reflect your unique personality, our Featured Products showcase the best of what the market has to offer.

Each product in this collection embodies our commitment to quality, performance, and design excellence. Whether you're looking to upgrade your living space, enhance your personal style, or find the perfect gift for someone special, our Featured Products are here to inspire and delight.

Join us on a journey through innovation as you browse through our thoughtfully selected range. We believe that every item tells a story, and we're excited to share these stories with you. Check back regularly, as we're always adding new and exciting products to keep you at the forefront of trends and advancements.

Indulge in the world of modern design and practicality. Explore our Featured Products today and elevate your lifestyle to new heights.
        </p>
      </div>
      <div className="bottom">
        {error
          ? "Something went wrong!"
          : loading
          ? "loading"
          : data?.map((item) => <Card item={item} key={item.id} />)}
      </div>
    </div>
  );
};

export default FeaturedProducts;
