// Import necessary modules and styles
import React from "react";
import "./Card.scss"; // Assuming Card component has associated styles in Card.scss
import { Link } from "react-router-dom";

// Functional component 'Card' which receives 'item' as a prop
const Card = ({ item }) => {
  // Log the 'item' prop to the console
  console.log(item);

  return (
    // Wrap the entire card in a Link that navigates to a specific product page
    <Link className="link" to={`/product/${item.id}`}>
      <div className="card"> {/* Card container */}
        <div className="image"> {/* Image container */}
          {/* Show "New Season" span if item is new */}
          {item?.attributes.isNew && <span>New Season</span>}

          {/* Main product image */}
          <img
            src={
              process.env.REACT_APP_UPLOAD_URL + item.attributes?.img?.data?.attributes?.url
            }
            alt=""
            className="mainImg"
          />

          {/* Second product image */}
          <img
            src={
              process.env.REACT_APP_UPLOAD_URL + item.attributes?.img2?.data?.attributes?.url
            }
            alt=""
            className="secondImg"
          />
        </div>

        {/* Product title */}
        <h2>{item?.attributes.title}</h2>

        <div className="prices"> {/* Price container */}
          {/* Display either 'oldPrice' or 'price + 20' */}
          <h3>${item.oldPrice || item?.attributes.price + 20}</h3>

          {/* Display the actual product price */}
          <h3>${item?.attributes.price}</h3>
        </div>
      </div>
    </Link>
  );
};

export default Card;
