import React from "react";
import "./Card.scss";
import { Link } from "react-router-dom";

const Card = ({ item }) => {
  if (!item) {
    return null; // Return early if 'item' is undefined
  }

  const { attributes } = item;

  return (
    <Link className="link" to={`/product/${item.id}`}>
      <div className="card">
        <div className="image">
          {attributes?.isNew && <span>New Season</span>}
          <img
            src={
              process.env.REACT_APP_UPLOAD_URL +
              (attributes.img.data.attributes.url || "")
            }
            alt=""
            className="mainImg"
          />
          {attributes.img2.data?.attributes?.url && ( // Check if img2 is available
            <img
              src={
                process.env.REACT_APP_UPLOAD_URL +
                attributes.img2.data.attributes.url
              }
              alt=""
              className="secondImg"
            />
          )}
        </div>
        <h2>{attributes?.title || "Title not available"}</h2>
        <div className="prices">
          <h3>${item.oldPrice || (attributes?.price + 20)}</h3>
          <h3>${attributes?.price}</h3>
        </div>
      </div>
    </Link>
  );
};

export default Card;
