// Import necessary modules and styles
import React from "react";
import Card from "../Card/Card"; // Assuming the Card component is located in the specified path
import "./FeaturedProducts.scss"; // Assuming FeaturedProducts component has associated styles in FeaturedProducts.scss
import useFetch from "../../hooks/useFetch"; // Assuming you're using a custom hook for fetching data

// Functional component 'FeaturedProducts' which receives 'type' as a prop
const FeaturedProducts = ({ type }) => {
  // Use the custom hook 'useFetch' to fetch data based on the 'type'
  const { data, loading, error } = useFetch(
    `/products?populate=*&[filters][type][$eq]=${type}&[filters][img][$exists]=true`
  );

  return (
    <div className="featuredProducts"> {/* Container for the featured products */}
      <div className="top"> {/* Top section */}
        <h1>{type} products</h1> {/* Display the type of products */}
        <p>
          {/* Introduction text */}
          Each product in this collection embodies our commitment to quality, performance, and design excellence. Whether you're looking to upgrade your living space, enhance your personal style, or find the perfect gift for someone special, our Featured Products are here to inspire and delight.
          <br />
          <br />
          We believe that every item tells a story, and we're excited to share these stories with you.
          <br />
          <br />
        </p>
      </div>
      <div className="bottom"> {/* Bottom section for displaying products */}
        {/* Display an error message if there's an error */}
        {error
          ? "Something went wrong!"
          // Display "loading" message while data is being fetched
          : loading
          ? "loading"
          // Map over the fetched 'data' and render 'Card' component for each item
          : data?.map((item) => <Card item={item} key={item.id} />)}
      </div>
    </div>
  );
};

export default FeaturedProducts;
