import React from "react";
import "./Card.scss";
import { Link } from "react-router-dom";

const Card = ({ item }) => {
  if (!item) {
    return null; // or you can return a placeholder or error message
  }

  const imgSrc = item?.attributes?.img?.data?.attributes?.url || "";
  const img2Src = item?.attributes?.img2?.data?.attributes?.url || "";

  return (
    <Link className="link" to={`/product/${item.id}`}>
      <div className="card">
        <div className="image">
          {item?.attributes?.new && <span>New Season</span>}
          {imgSrc && (
            <img src={process.env.REACT_APP_UPLOAD_URL + imgSrc} alt="" className="mainImg" />
          )}
          {img2Src && (
            <img src={process.env.REACT_APP_UPLOAD_URL + img2Src} alt="" className="secondImg" />
          )}
        </div>
        <h2>{item?.attributes?.title}</h2>
        <div className="prices">
          <h3>${item.oldPrice || item?.attributes?.price + 20}</h3>
          <h3>${item?.attributes?.price}</h3>
        </div>
      </div>
    </Link>
  );
};

export default Card;
