import React from "react";
import "./style.css"; // Import your CSS stylesheet

const Slider = ({ children }) => {
  return (
    <div className="slider">
      {children.map((child, index) => (
        <div className="slide" key={index}>
          {child}
        </div>
      ))}
    </div>
  );
};

const Slide = ({ children }) => {
  return <div className="slide">{children}</div>;
};

const App = () => {
  return (
    <html lang="en">
      <head>
        <meta charset="utf-8" />
        <title>Horizontal slider</title>
        <link rel="stylesheet" href="style.css" />
      </head>
      <body>
        <Slider>
          <Slide>
            <p>Slide one</p>
          </Slide>
          <Slide>
            <p>Slide two</p>
          </Slide>
          <Slide>
            <p>Slide three</p>
          </Slide>
          <Slide>
            <p>Slide four</p>
          </Slide>
        </Slider>
      </body>
    </html>
  );
};

export default App;
